import React from "react";
import axios from "axios";
import Header from "./Header";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = [];
    this.pokemonArray = [];
    this.getUserAsync = this.getUserAsync.bind(this);
  }
  async fetchPokemonApi() {
    try {
      let response = await axios({
        method: "get",
        url: `https://pokeapi.co/api/v2/pokemon?limit=1&offset=200`,
      });
      console.log(response.data);
      this.pokemonArray.push(response.data);
      return response;
    } catch (err) {
      console.error(err);
    }
  }
  componentDidMount() {
    this.getUserAsync();
  }

  render() {
    return (
      <div className="App">
        <Header />
      </div>
    );
  }
}

export default App;
