import React from "react";
import axios from "axios";
import Header from "./Header";
import Card from "./Card";

class App extends React.Component {
  constructor() {
    super();
    this.state = {
      pokemons: [],
    };
    this.fetchPokemonApi = this.fetchPokemonApi.bind(this);
  }

  fetchPokemonApi() {
    console.log(this.state.pokemons);
  }
  componentDidMount() {
    var arr = [];

    for (var id = 1; id <= 150; id++) {
      try {
        let response = axios({
          method: "get",
          url: `https://fizal.me/pokeapi/api/v2/id/${id}.json`,
        });

        arr.push(response.data);
      } catch (err) {
        console.error(err);
      }
      this.setState({ pokemons: arr });
    }
  }

  render() {
    return (
      <div className="App">
        <Header />

        <div id="grid">
          {this.state.pokemons.map(function (item) {
            console.log(this.state.pokemons);
            console.log("here");
            return (
              <Card name={item.name} imgUrl={item.sprites.front_default} />
            );
          })}
        </div>
      </div>
    );
  }
}

export default App;
