import React from "react";
import axios from "axios";
import Header from "./Header";
import Card from "./Card";

class App extends React.Component {
  constructor() {
    super();
    this.state = {
      pokemons: [],
    };
    this.fetchPokemonApi = this.fetchPokemonApi.bind(this);
  }

  async fetchPokemonApi(id) {
    try {
      let response = await axios({
        method: "get",
        url: `https://fizal.me/pokeapi/api/v2/id/${id}.json`,
      });
      // this.setState({ pokemons: response });
      console.log(this.state.pokemons);
      return response.data;
    } catch (err) {
      console.error(err);
    }
  }
  componentDidMount() {
    var pokemonsArr = [];
    for (var id = 1; id <= 150; id++) {
      pokemonsArr.push(this.fetchPokemonApi(id));
    }
    console.log(pokemonsArr);
  }

  render() {
    return (
      <div className="App">
        <Header />

        <div id="grid">
          {/* {this.state.data
            ? this.state.pokemons.map(function (item) {
                console.log(item);
                return <Card name={item.name} imgUrl={item.url} />;
              })
            : null} */}
        </div>
      </div>
    );
  }
}

export default App;
