    //   });
    //   console.log(response.data);
    //   // this.setState({ data: response.data.results.url });
    // } catch (err) {
    //   console.error(err);
    // }
  }
  componentDidMount() {
    this.fetchPokemonApi();
  }

  render() {
    return (
      <div className="App">
        <Header />

        <div id="grid">
          {/* {this.state.data
            ? this.state.data.map(function (item) {
                console.log(item);
                return <Card name={item.name} imgUrl={item.url} />;
              })
            : null} */}
        </div>
      </div>
    );
  }
}

export default App;
