import React from "react";
import axios from "axios";
import Header from "./Header";
import Card from "./Card";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = [];
    this.fetchPokemonApi = this.fetchPokemonApi.bind(this);
  }
  async fetchPokemonApi() {
    try {
      let response = await axios({
        method: "get",
        url: `https://pokeapi.co/api/v2/pokemon?limit=100&offset=200`,
      });
      console.log(response.data);
      this.setState({ data: response.data.results.url });
    } catch (err) {
      console.error(err);
    }
  }
  componentDidMount() {
    this.fetchPokemonApi();
  }

  render() {
    return (
      <div className="App">
        <Header />

        <div id="grid">
          {this.state.data
            ? this.state.data.map(function (item) {
                console.log(item);
                return <Card name={item.name} imgUrl={item.url} />;
              })
            : null}
        </div>
      </div>
    );
  }
}

export default App;
