import React from "react";
import axios from "axios";
import Header from "./Header";
import Card from "./Card";

class App extends React.Component {
  constructor() {
    super();
    this.state = {
      pokemons: [],
    };
    var arr = [];

    for (var id = 1; id <= 3; id++) {
      try {
        let response = axios({
          method: "get",
          url: `https://fizal.me/pokeapi/api/v2/id/${id}.json`,
        });
        // console.log(this.state.pokemons);
        // arr.push(this.state.pokemons);
        arr.push(response.data);
        this.setState({ pokemons: arr });
      } catch (err) {
        console.error(err);
      }
    }
    this.fetchPokemonApi = this.fetchPokemonApi.bind(this);
  }

  async fetchPokemonApi() {
    var arr = [];

    for (var id = 1; id <= 1; id++) {
      try {
        let response = await axios({
          method: "get",
          url: `https://fizal.me/pokeapi/api/v2/id/${id}.json`,
        });
        // console.log(this.state.pokemons);
        // arr.push(this.state.pokemons);
        arr.push(response.data);
        this.setState({ pokemons: arr });
      } catch (err) {
        console.error(err);
      }
    }

    // console.log(arr);
    console.log(this.state.pokemons);
  }
  componentDidMount() {
    this.fetchPokemonApi();
  }

  render() {
    return (
      <div className="App">
        <Header />

        <div id="grid">
          {this.state.data
            ? this.state.pokemons.map(function (item) {
                console.log(this.state.pokemons);
                console.log("here");
                return (
                  <Card name={item.name} imgUrl={item.sprites.front_default} />
                );
              })
            : "null"}
        </div>
      </div>
    );
  }
}

export default App;
